package dedalus.try1;

public class TryDoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int loop = 1;
		
		do {
		System.out.println(loop);	
		loop = loop + 1;
		}
		while (loop>5);
		System.out.println("exiting...");
		System.exit(0);
		
	}

}
